
public class LargestNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=34,b=26,c=76;
		if (a>=b && a>=c) {
			System.out.println("A is Largest");
		}
		else if (b>=c && b>=a) {
			System.out.println("B is Largest");
		}
		else {
			System.out.println("C is Largest");
		}
	}

}
